const choices=["rock","paper","scissors"],playerDisplay=document.getElementById("playerDisplay"),computerDisplay=document.getElementById("computerDisplay"),resultDisplay=document.getElementById("resultDisplay"),playerScoreDisplay=document.getElementById("playerScoreDisplay"),computerScoreDisplay=document.getElementById("computerScoreDisplay");let playerScore=0,computerScore=0;function playGame(playerChoice){const computerChoice=choices[Math.floor(3*Math.random())];let result="";if(playerChoice===computerChoice)result="IT'S A TIE";else switch(playerChoice){case"rock":result="scissors"===computerChoice?"YOU WIN! ðŸ¤©":"YOU LOSE! ðŸ˜­";break;case"paper":result="rock"===computerChoice?"YOU WIN! ðŸ¤©":"YOU LOSE! ðŸ˜­";break;case"scissors":result="paper"===computerChoice?"YOU WIN! ðŸ¤©":"YOU LOSE! ðŸ˜­"}switch(playerDisplay.textContent=`PLAYER: ${playerChoice}`,computerDisplay.textContent=`COMPUTER: ${computerChoice}`,resultDisplay.textContent=result,resultDisplay.classList.remove("greenText","redText"),result){case"YOU WIN! ðŸ¤©":resultDisplay.classList.add("greenText"),playerScore++,playerScoreDisplay.textContent=playerScore;break;case"YOU LOSE! ðŸ˜­":resultDisplay.classList.add("redText"),computerScore++,computerScoreDisplay.textContent=computerScore}}